            // .catch( err =>{
            //     console.log(err);
            // })
        }

    return( 

        <FeedMain>

            <FeedContent>

                {feeds.reverse().map(e =>  
                    <FeedItem key={e._id} > 
                    <DeleteBtn onClick={()=>{handleClick(e._id)} }>Delete</DeleteBtn>

                     <Title>
                        {e.title}
                     </Title>
                     <Content>
                        {e.description}
                     </Content> 

                    </FeedItem>
                )}

            </FeedContent>



        </FeedMain>

    )
}
export default DeletePost
