import React,{useState ,useEffect}  from 'react'
import styled from 'styled-components'
import '../styles/admin.css'
import axios from 'axios'
import Feed from '../components/feed'

const AdminLog = styled.div`
    display: flex;
    background-color: white;
    margin-top: 5rem;
    align-self: center;
    align-content: center;
    align-items: center;
    width: 20rem;
    border-radius: 2rem;
    height:30rem;
    padding: 3rem;
    display: inline-block;
    border-style: solid;
    border-color: #00a2ff;
    margin-left: 42.5rem;

`
const ColoredLine = styled.hr`
            color:#00a2ff;
            background-color: #00a2ff;
            height: 1px;
            display: flex;
            width: 16rem;
            align-self: center;
            margin-top: 0.5rem;
            margin-right: 3rem;

`
const ColoredLine1 = styled.hr`
            color:#00a2ff;
            background-color: #00a2ff;
            height: 1px;
            display: flex;
            width: 100rem;
            align-self: center;
            margin-top: 0.5rem;
            margin-right: 3rem;

`
const AdminPanel=styled.div`


    background-color: white;
    margin-top: 5rem;
    align-self: center;
    align-content: center;
    align-items: center;
    width: 105rem;
    border-radius: 1rem;
    height: auto;
    padding: 2rem;
    display: inline-block;
    border-style: solid;
    border-color: #00a2ff;
    margin-left: 2rem;


`

const newPost = styled.div`
    background-color: #f8f6f6;
    width: 10rem;
    border-color: #c0bcbc;
    border-width: 1px;
    border-radius: 20px;
    height: 50rem;
    display: inline;

`
const DeleteTab=styled.div`
     width: 70rem;
     align-items: center;
     display: inline-block;
     overflow: scroll;
     height: 35rem;
     display: inline;
     margin-left: 15rem;
`

const Admin= ()=>{
    const [username,setUsername] =useState([])
    const [password,setPassword] =useState([])
    const [message,setMessage] =useState([''])
    const [isAdmin,setIsAdmin] =useState(false)



    const handleUsernameChange=(event)=>{

        setUsername(event.target.value)
    }
    const handlePasswordChange=(event)=>{
        setPassword(event.target.value)
    }
    const handleSubmit=(event)=>{

        var payload = { 
            username: username,
            password: password
        }
        axios.post(`http://localhost:5000/api/auth/admin`,payload)
        .then(res => {
         console.log(res.data);
         setMessage(res.data)
         setIsAdmin(true)

       }).catch((err)=> {console.log(err)})
   }

   //New post---------------------------------------------

   const [title,setTitle] =useState([])
   const [content,setContent] =useState([])

   const handleTitleChange=(event)=>{
        setTitle(event.target.value)
   }
   const handleContentChange=(event)=>{
    setTitle(event.target.value)
   }



    if (isAdmin==false){
        return(
            <AdminLog> 
                 <h1>Login As Admin</h1>
                 <ColoredLine/>

                 <input 
                       type="text" 
                       placeholder="Name"
                       value={username} 
                       onChange={handleUsernameChange}/>
                <input 
                       type="text" 
                       placeholder="Password"
                       value={password} 
                       onChange={handlePasswordChange}/>

                <button id="loginBtn" type="submit" onClick={handleSubmit}>Login</button>
                <p>{message}</p>
            </AdminLog>

        )
    }else{
        return(
            < AdminPanel>
                <h2>Admin Panel</h2>
                <ColoredLine1/>
                <h2 >New Post</h2>
                <newPost>
                    <input
                        id="title"
                        type="text"
                        placeholder="Title"
                        value={title}
                    ></input>
                    <input
                        id="content"
                        type="text"
                        placeholder="Content"
                    ></input>

                </newPost>

                <DeleteTab>
                 <h3>Click To Delete</h3>
                 <Feed/>
                </DeleteTab>


            </AdminPanel>

        )
    }

}
export default Admin
